function CategoriesTiles() {
  const cats: { label: string; icon: React.ComponentType<{ className?: string }> }[] = [
    { label: "Оптика та лампи", icon: OpticsIcon },
    { label: "Акумуляторні батареї", icon: BatteryIcon },
    { label: "Масла та рідини", icon: OilsIcon },
    { label: "Шини, камери, стрічки", icon: Tires },
    { label: "Запасні частини до автомобілів", icon: CarPartsIcon },
    { label: "Запчастини до тракторів", icon: TractorPartsIcon },
    { label: "Універсальні та стандартні вироби", icon: UniversalIcon },
    { label: "Генератори", icon: GeneratorIcon },
    { label: "Трактори", icon: Traktors },
  ];

  const scrollRef = React.useRef<HTMLDivElement>(null);
  const scroll = (dir: "left" | "right") => {
    const el = scrollRef.current;
    if (!el) return;
    el.scrollBy({ left: (dir === "left" ? -1 : 1) * el.clientWidth * 0.8, behavior: "smooth" });
  };

  return (
    <div className="relative">
      {/* м’які шторки */}
      <div
        className="pointer-events-none absolute left-0 top-0 h-full w-20 z-30"
        style={{
          background:
            "linear-gradient(to right, rgb(250 250 250 / 1) 0%, rgb(250 250 250 / 0.9) 55%, rgb(250 250 250 / 0) 100%)",
        }}
      />
      <div
        className="pointer-events-none absolute right-0 top-0 h-full w-20 z-30"
        style={{
          background:
            "linear-gradient(to left,  rgb(250 250 250 / 1) 0%, rgb(250 250 250 / 0.9) 55%, rgb(250 250 250 / 0) 100%)",
        }}
      />

      {/* кнопка вліво */}
      <button
        aria-label="Попередній"
        onClick={() => scroll("left")}
        className="cursor-pointer absolute left-4 top-1/2 -translate-y-1/2 z-40 grid h-12 w-12 place-items-center rounded-xl border-2 border-[rgb(0,163,211)] bg-transparent hover:bg-[rgb(0,163,211)]/10 transition hover:scale-105 focus:outline-none shadow-sm"
        style={{ isolation: "isolate" }}
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" viewBox="0 0 24 24" fill="none" stroke="rgb(0,163,211)" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
          <path d="m15 18-6-6 6-6" />
        </svg>
      </button>

      {/* стрічка плиток */}
      <div ref={scrollRef} className="flex gap-3 sm:gap-4 overflow-hidden scroll-smooth px-24">
        {cats.map((c, i) => {
          const Icon = c.icon;
          return (
            <a
              key={c.label}
              href="#"
              className={`min-w-[140px] sm:min-w-[160px] cursor-pointer group flex flex-col items-center justify-start rounded-2xl border border-zinc-200 bg-white p-4 shadow-sm transition hover:shadow-md hover:bg-zinc-50 ${
                i === 0 ? "ml-4" : i === cats.length - 1 ? "mr-4" : ""
              }`}
            >
              <div className="grid h-12 w-12 place-items-center mb-2 text-[color:var(--brand)]" style={{ ["--brand"]: BRAND } as React.CSSProperties}>
                <Icon className="h-12 w-12 transition-transform group-hover:scale-110" />
              </div>
              <div className="text-center text-xs sm:text-sm font-medium text-zinc-700 leading-tight line-clamp-2">{c.label}</div>
            </a>
          );
        })}
      </div>

      {/* кнопка вправо */}
      <button
        aria-label="Наступний"
        onClick={() => scroll("right")}
        className="cursor-pointer absolute right-4 top-1/2 -translate-y-1/2 z-40 grid h-12 w-12 place-items-center rounded-xl border-2 border-[rgb(0,163,211)] bg-transparent hover:bg-[rgb(0,163,211)]/10 transition hover:scale-105 focus:outline-none shadow-sm"
        style={{ isolation: "isolate" }}
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" viewBox="0 0 24 24" fill="none" stroke="rgb(0,163,211)" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
          <path d="m9 18 6-6-6-6" />
        </svg>
      </button>
    </div>
  );
}





