function IconButtonSoft({ children, onClick, label }: { children: React.ReactNode; onClick?: () => void; label: string }) {
  return (
    <button
      type="button"
      aria-label={label}
      onClick={(e) => { e.preventDefault(); onClick?.(); }}
      className="cursor-pointer grid h-9 w-9 place-items-center rounded-full bg-white text-zinc-600 shadow-sm ring-1 ring-zinc-200 transition hover:scale-105 hover:text-zinc-900 hover:bg-zinc-50"
    >
      {children}
    </button>
  );
}

function Stars({ value = 0 }: { value?: number }) {
  const stars = Array.from({ length: 5 }).map((_, i) => {
    const n = i + 1;
    const diff = (value ?? 0) - i;
    if (diff >= 1) return <Star key={n} className="h-4 w-4 fill-yellow-400 text-yellow-400" />;
    if (diff >= 0.5) return <StarHalf key={n} className="h-4 w-4 fill-yellow-400 text-yellow-400" />;
    return <Star key={n} className="h-4 w-4 text-zinc-400" />;
  });
  return <div className="flex items-center gap-0.5">{stars}</div>;
}

